import React, {useState} from 'react';
import './Components.css';

import imageLogo from './resources/bcnvisionlogo.png';

const HeaderMenuItem = (props) => {  
  const [onMouseOverValue, setOnMouseOverValue] = useState([true])
  const className = onMouseOverValue ? "Header-menu-item-class" : "Header-menu-item-class-highlighted";
  return (
    <li className={className}>
        <a href={props.ruta} onMouseEnter={() => setOnMouseOverValue(false)} onMouseLeave={() => setOnMouseOverValue(true)}>
          {/* {props.texto} */}
          <h5>{props.texto}</h5>
        </a>
    </li>
  );
}

export default HeaderMenuItem;
