import React from 'react';
import './Components.css';

import imageLogo from './resources/bcnvisionlogo.png';

const HeaderMenuItem = (props) => {  
  const [onMouseOverValue, setOnMouseOverValue] = useState([true])

  return (
    <li className="Header-menu-item-class">
        <a onMouseEnter={() => setOnMouseOverValue(false)} onMouseLeave={() => setOnMouseOverValue(true)}>
          {props.Texto}
        </a>
    </li>
  );
}

export default HeaderMenuItem;
